import React, { useState } from 'react';


const App = () => {

  const [good, setGood] = useState({gdclicks: 0, gdpts: 0})
  const [neutral, neutralpts, setNeutral] = useState({neutclicks: 0, neutpts: 0})
  const [bad, setBad] = useState({badclicks: 0, badpts: 0})

  const handleGoodClick = () => setGood({gdclicks: good.gdclicks + 1, gdpts: good.gdpts + 1})


  return (
    <div>
      <h1>Give Feedback</h1>
      <button onClick={() => setGood(good+1)}>Good</button>        
      <button onClick={() => setNeutral(neutral+1)}>Neutral</button>
      <button onClick={() => setBad(bad+1)}>Bad</button>
      <br />
      <h1>Statistics</h1>
      <p>Good {good}</p>
      <p>Neutral {neutral}</p>
      <p>Bad {bad}</p>

    </div>
)
}

export default App;
